ecc_agent_script_file.script:
## Import PowerShell module for AD Get-WindowsFeature -Name RSAT-AD-Powershell
## Get forests available to current $Forests = (GET-ADTrust -Filter *).target
## Obtain domain controllers and subnets per forest ForEach($Forest in $Forests){ $DcList = (Get-ADForest -Identity $Forest).Domains | %{Get-ADDomainController -Filter * -Server $_}| Select Site, Name, Domain $Subnets = Get-ADReplicationSubnet -filter * -Properties * | Select Name, Site, Location, Description $ResultsArray = @()
ForEach ($Subnet in $subnets) {
$SiteName = "" If($Subnet.Site -ne $null) {$SiteName = $Subnet.Site.Split(',')[0].Trim('CN=')}
## Check if a domian controller exists in site (not used in return) $DcInSite = $False If($DCList.Site -Contains $SiteName) {$DcInSite = $True}
## Build return object from information above $RA = New-Object PSObject $RA | Add-Member -type NoteProperty -name "Subnet" -Value $Subnet.Name $RA | Add-Member -type NoteProperty -name "SiteName" -Value $SiteName $RA | Add-Member -type NoteProperty -name "SiteDesc" -Value $Subnet.Description
$ResultsArray += $RA
} } ## Convert array to JSON and only bring back unique subnets $ResultsArray | Sort-Object -Property Subnet -Unique | ConvertTo-Json
